//tstbtree.cc#include "btnode.h"#include "btnode.cpp"#include "btree.h"#include "btree.cpp"#include <iostream>const char * keys = "CSDTAMPIBWNGURKEHOLJYQZFXV";const int BTreeSize = 4;main(int argc, char * argv){	int result, i;	char name[11] = "testbt.dat";	BTree <char> bt(BTreeSize);	result = bt.Create(name, ios::in | ios::out);	if (!result) { cout << "Please delete testbt.dat" << endl; return 0; }	for (i = 0; i < 26; i++)	{		cout << "Inserting " << keys[i] << endl;		result = bt.Insert(keys[i], i);		bt.Print(cout);	}	bt.Search(1, 1);	return 1;}